INSERT INTO `posts` (`id`, `title`, `category_id`, `short_desc`, `long_desc`, `slug`, `image`, `division_id`, `distric_id`, `status`, `created_at`, `updated_at`, `heding_news`, `first_section`, `second_section`, `three_section`, `view_count`, `subcategory_id`, `admin_id`, `upzila_id`, `four_section`, `five_section`) VALUES
(4, '২১ মার্চ শতভাগ বিদ্যুতের ঘোষণা দেবেন প্রধানমন্ত্রী', 1, '<p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\"><em><span style=\"font-weight: bolder;\">২১ মার্চ ১ হাজার ৩২০ মেগাওয়াট পায়রা কয়লা বিদ্যুৎ কেন্দ্র উদ্বোধনের মাধ্যমে দেশে শতভাগ বিদ্যুতের ঘোষণা দেবেন প্রধানমন্ত্রী শেখ হাসিনা । সোমবার (১৪ মার্চ) বিদ্যুৎ, জ্বালানি ও খনিজ সম্পদ প্রতিমন্ত্রী নসরুল হামিদ রাজধানীর বিদ্যুৎ ভবনে এক সংবাদ সম্মেলনে একথা জানান। ফোরাম ফর এনার্জি রিপোর্টার্স বাংলাদেশ (এফইআরবি) এবং নর্থওয়েস্ট পাওয়ার জেনারেশন কোম্পানি (এনডব্লিউপিজিসিএল) যৌথভাবে এ ‘মিট দ্য প্রেস’-এর আয়োজন করে।</span></em><br></p>', '<p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\">বাংলাদেশ শতভাগ দূষণমুক্ত সুপার ক্রিটিক্যাল প্রযুক্তির একটি কয়লা বিদ্যুৎ কেন্দ্র শুরু করতে যাচ্ছে উল্লেখ করে প্রতিমন্ত্রী বলেন, বর্তমান সরকার দেশের সব চর ও প্রত্যন্ত অঞ্চলকে বিদ্যুতের আওতায় নিয়ে এসেছে। &nbsp;নসরুল হামিদ বলেন, সরকার বাংলাদেশের জাতির পিতা বঙ্গবন্ধু শেখ মুজিবুর রহমানের জন্ম শতবার্ষিকী উপলক্ষে দেশকে শতভাগ বিদ্যুতের আওতায় আনার লক্ষ্যমাত্রা গ্রহণ করেছিল এবং ২১ মার্চ সরকার তার এ প্রতিশ্রুতি বাস্তবায়ন করতে যাচ্ছে।</p><p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\">তিনি বলেন, প্রধানমন্ত্রী শেখ হাসিনার দূরদর্শী, সাহসী ও সময়োপযোগী সিদ্ধান্তের ফলে বিগত ১৩ বছরে বাংলাদেশের বিদ্যুৎ খাতে অভূতপূর্ব সাফ্যল্য এসেছে।<br>&nbsp;নসরুল হামিদ বলেন, আমাদের দেশের বিদ্যুৎ উৎপাদন সক্ষমতা ৫ গুণের বেশি বেড়েছে। ২০০৯ সালের জানুয়ারির ৪ হাজার ৯৪২ মেগাওয়াট থেকে বিদ্যুৎ উৎপাদন বৃদ্ধি পেয়ে ২০২২ সালের ফেব্রুয়ারিতে ২৫ হাজার ৫১৪ মেগাওয়াটে দাঁড়িয়েছে। এর মধ্যে নবায়নযোগ্য জ্বালানিও রয়েছে। ১১৬০ মেগাওয়াট বিদ্যুৎ আমদানি এবং ১৯ হাজার ৬২৬ মেগাওয়াট বিদ্যুৎ জাতীয় গ্রিডে যোগ করা হয়েছে।</p><p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\"><br></p><p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\">তিনি বলেন, শীতকাল ও অফ-পিক আওয়ারে উদ্বৃত্ত বিদ্যুৎ রপ্তানির পরিকল্পনা নেওয়া হয়েছে। মোট ৫ হাজার ২১৩ সার্কিট কিলোমিটার ট্রান্সমিশন লাইন বসানো এবং মোট ৩ লাখ ৬১ হাজার কিলোমিটার বিদ্যুৎ বিতরণকারী তার বসানো হয়েছে। ৩.১৩ কোটি নতুন গ্রাহককে সংযোগ দেওয়া হয়েছে।’</p><p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\">মাথাপিছু বিদ্যুৎ উৎপাদন ২২০ কিলোওয়াট থেকে বেড়ে ৫৬০ কিলোওয়াটে দাঁড়িয়েছে এবং বিদ্যুৎ বিতরণ ক্ষতি ৫.৮৫ শতাংশ হ্রাস পেয়েছে। ৪৮.৭৭ লাখ স্মার্ট প্রি-পেইড মিটার স্থাপন এবং সেচ কার্যে নিরবিচ্ছিন্ন বিদ্যুৎ সরবরাহ নিশ্চিত করা হয়েছে।</p><p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\"><br></p><p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\">তিনি বলেন, বাংলাদেশ-চায়না পাওয়ার কোম্পানি লি. সফলভাবে পায়রা ১৩২০ মেগাওয়াট বিদ্যুৎ কেন্দ্রের ইউনিট-১ ও ইউনিট-২ এ বাণিজ্যিক উৎপাদন সফলভাবে শুরু করেছে।&nbsp;</p><p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\">সফলভাবে ৬৩ লাখ বাড়িতে সোলার সিস্টেম স্থাপন করা হয়েছে জানিয়ে তিনি আরও বলেন, এখন পর্যন্ত এদেশে পায়রা ১৩২০ মেগাওয়াট বিদ্যুৎ কেন্দ্রটি সর্ববৃহৎ বিদ্যুৎ কেন্দ্র। বাংলাদেশ এখন বিশ্বের আল্ট্রা সুপারক্রিটিক্যাল প্রযুক্তি ব্যবহারকারী দেশগুলোর মধ্যে ১৩তম।</p><p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\">প্রতিমন্ত্রী দেশের টেকসই উন্নয়ন ও অর্থনৈতিক প্রবৃদ্ধিতে বিদ্যুৎখাত গুরুত্বপূর্ণ ভূমিকা পালন করছে উল্লেখ করে তিনি বলেন, ‘যদি আপনাদের সহযোগিতায় বিদ্যুৎ খাতের পরিকল্পনাগুলো সফলভাবে বাস্তবায়িত হয়, তবে বাংলাদেশ ২০৪১ সাল নাগাদ প্রধানমন্ত্রীর রূপকল্প অনুযায়ী একটি উন্নত ও সমৃদ্ধ দেশে পরিণত হবে।&nbsp;</p><p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\">বিদ্যুৎ সচিব মো. হাবিবুর রহমান বলেন, বাংলাদেশ দক্ষিণ এশিয়ার শতভাগ বিদ্যুতের দেশ। সাবমেরিন ক্যাবলের মাধ্যমে দেশের চর সোনারমপুর, আশুগঞ্জ, রাঙ্গাবালি, মনপুরা, সন্দীপ, হাতিয়া, নিঝুম দ্বীপ ও তুকুবাদিয়ায় বিদ্যুৎ সরবরাহ কর হয়েছে।</p><p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\">অনুষ্ঠানে অন্যান্যের মধ্যে উপস্থিত ছিলেন বিপিডিবি চেয়ারম্যান মাহাবুবুর রহমান, পাওয়ার সেলের মহাপরিচালক মোহাম্মদ হোসেইন, নর্থ ওয়েস্ট পাওয়ার জেনারেশন কোম্পানির প্রধান নির্বাহী কর্মকর্তা খোরশেদুল আলম। মিট দ্য প্রেসে স্বাগত বক্তব্য রাখেন এফইআরবির চেয়ারম্যান শামীম জাহাঙ্গীর। অনুষ্ঠানটি সঞ্চালনা করেন নির্বাহী পরিচালক রিশান নাসরুল্লাহ্।</p><p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\">একুশে সংবাদ/এসএস</p>', '21-marc-stvag-bidzuter-ghoshna-deben-prdhanmntree', '1647322130.jpg', 3, 18, '1', '2022-03-14 23:28:50', '2022-04-03 15:08:11', '1', NULL, NULL, NULL, '1', 1, 1, NULL, NULL, NULL),
(5, 'ক্ষেপণাস্ত্র হামলায় নিহত ২০, পরস্পরকে দুষছে রাশিয়া-ইউক্রেন', 3, '<p><em style=\"color: rgb(0, 0, 0); font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif; font-size: 18px; text-align: justify;\"><span style=\"font-weight: bolder;\">ইউক্রেনের পূর্বাঞ্চলীয় দোনেতস্ক অঞ্চলে ক্ষেপণাস্ত্র হামলায় ২০ জন সাধারণ মানুষ নিহত হয়েছে বলে অভিযোগ করেছে রাশিয়া। হামলায় আরও ২৮ জন আহত হয়েছেন জানিয়ে মস্কোর দাবি, ইউক্রেনের সামরিক বাহিনী এই হামলা করেছে।</span></em><br></p>', '<p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\">তবে মস্কোর এই অভিযোগ অস্বীকার করেছে কিয়েভ। সোমবার (১৪ মার্চ) এক প্রতিবেদনে এই তথ্য জানিয়েছে বার্তাসংস্থা রয়টার্স ও সংবাদমাধ্যম বিবিসি। অবশ্য দোনেতস্কে হামলা বা হতাহতের বিষয়ে ইউক্রেনকে দোষারোপ করলেও রাশিয়ার প্রতিরক্ষা মন্ত্রণালয় কোনো প্রমাণ সামনে আনেনি। তবে ইউক্রেন ওই হামলার দায় অস্বীকার করে দাবি করেছে, রাশিয়ার সামরিক বাহিনীর নিক্ষেপ করা ক্ষেপণাস্ত্রে এসব মানুষ নিহত হয়েছেন। রয়টার্স স্বাধীনভাবে উভয় পক্ষের বিবৃতি যাচাই করতে পারেনি বলে জানিয়েছে বার্তাসংস্থাটি।</p><p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\">সোমবার রুশ গণমাধ্যম জানায়, ইউক্রেনের সামরিক বাহিনীর ক্ষেপণাস্ত্র দোনেতস্কের কেন্দ্রে আঘাত হানে এবং এতে ২০ জন নিহত ও আরও অনেকে আহত হন। এই ঘটনার জন্য কিয়েভকে যুদ্ধাপরাধের জন্য অভিযুক্ত করে দেশটি।</p><p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\"><br></p><p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\"><br></p><p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\">অপরদিকে রাশিয়ার এই অভিযোগের জবাবে ইউক্রেনের প্রতিরক্ষা মন্ত্রণালয় দাবি করে, হামলায় ব্যবহৃত মিসাইলটি ‘নিঃসন্দেহে রাশিয়ার ক্ষেপণাস্ত্র’ ছিল।</p><p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\">টেলিভিশনে দেওয়া এক ভাষণে ইউক্রেনের সামরিক বাহিনীর মুখপাত্র লিওনিদ মাতিউখিন বলেন, এটি নিয়ে কথা বলেও কোনো লাভ নেই। একইসঙ্গে রাশিয়ার বিরুদ্ধে এই হামলাকে ‘প্রোপাগান্ডা’ হিসেবে ব্যবহার করার অভিযোগও সামনে আনে তিনি। দোনেতস্ক মূলত রুশপন্থি বিদ্রোহী-নিয়ন্ত্রিত বিচ্ছিন্নতাবাদী এলাকাগুলোর একটি যেখানে ইউক্রেনীয় বাহিনীর বিরুদ্ধে ২০১৪ সাল থেকে লড়াই চলছে।</p>', 'kshepnastr-hamlay-niht-20-prsprke-dushche-rasiya-iukren', '1647322726.jpg', NULL, 1, '1', '2022-03-14 23:38:46', '2022-04-05 23:22:08', '1', '1', NULL, NULL, '2', 1, 1, NULL, NULL, NULL),
(6, 'করোনায় আক্রান্ত সাড়ে ১১ লাখ, মৃত্যু আরও ৪ হাজার', 3, '<p><em style=\"color: rgb(0, 0, 0); font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif; font-size: 18px; text-align: justify;\"><span style=\"font-weight: bolder;\">করোনায় বিশ্বজুড়ে দৈনিক মৃত্যুর সংখ্যা বেড়েছে। তবে আগের দিনের তুলনায় কমেছে নতুন শনাক্ত রোগীর সংখ্যা। গত ২৪ ঘণ্টায় সারা বিশ্বে করোনায় আক্রান্ত হয়ে মারা গেছেন প্রায় ৪ হাজার মানুষ। একই সময়ে ভাইরাসটিতে নতুন করে আক্রান্তের সংখ্যা নেমে এসেছে প্রায় সাড়ে ১১ লাখে।</span></em><br></p>', '<p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\">গত ২৪ ঘণ্টায় বিশ্বজুড়ে সবচেয়ে বেশি সংক্রমণের ঘটনা ঘটেছে দক্ষিণ কোরিয়ায়। অন্যদিকে দৈনিক প্রাণহানির তালিকায় শীর্ষে রয়েছে রাশিয়া। প্রাণহানির তালিকায় এরপরই রয়েছে যুক্তরাষ্ট্র, দক্ষিণ কোরিয়া, ইন্দোনেশিয়া, ব্রাজিল ও ফ্রান্স। এতে বিশ্বব্যাপী করোনায় আক্রান্তের সংখ্যা ছাড়িয়েছে ৪৫ কোটি ৯৫ লাখের ঘর। অন্যদিকে মৃতের সংখ্যা ছাড়িয়েছে ৬০ লাখ ৬৬ হাজার।</p><p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\"><br>মঙ্গলবার (১৫ মার্চ) সকালে করোনাভাইরাসে আক্রান্ত, মৃত্যু ও সুস্থতার হিসাব রাখা ওয়েবসাইট ওয়ার্ল্ডোমিটারস থেকে পাওয়া সর্বশেষ তথ্য অনুযায়ী, গত ২৪ ঘণ্টায় সারা বিশ্বে করোনায় আক্রান্ত হয়ে মারা গেছেন ৩ হাজার ৯০৬ জন। অর্থাৎ আগের দিনের তুলনায় মৃত্যুর সংখ্যা বেড়েছে প্রায় চারশো। এতে বিশ্বজুড়ে মৃতের সংখ্যা পৌঁছেছে ৬০ লাখ ৬৬ হাজার ৮৯৯ জনে।</p><p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\"><br></p><p style=\"margin: 10px 0px; line-height: 26px; text-align: justify;\"><font color=\"#000000\" face=\"SolaimanLipiNormal, Helvetica, Arial, sans-serif\"><span style=\"font-size: 18px;\">অন্যদিকে দৈনিক প্রাণহানির তালিকায় শীর্ষে রয়েছে রাশিয়া। এই সময়ের মধ্যে রাশিয়ায় করোনায় আক্রান্ত হয়ে মারা গেছেন ৫৩৩ জন এবং নতুন করে করোনায় সংক্রমিত হয়েছেন ৪১ হাজার ৫৫ জন। এছাড়া মহামারির শুরু থেকে এ পর্যন্ত দেশটিতে মোট শনাক্ত রোগীর সংখ্যা ১ কোটি ৭৩ লাখ ৭৬ হাজার ২৪১ জন এবং মৃত্যু হয়েছে ৩ লাখ ৬১ হাজার ৩৪৪ জনের।</span></font></p><p style=\"margin: 10px 0px; line-height: 26px; text-align: justify;\"><font color=\"#000000\" face=\"SolaimanLipiNormal, Helvetica, Arial, sans-serif\"><span style=\"font-size: 18px;\"><br></span></font></p><p style=\"margin: 10px 0px; line-height: 26px; text-align: justify;\"><font color=\"#000000\" face=\"SolaimanLipiNormal, Helvetica, Arial, sans-serif\"><span style=\"font-size: 18px;\"><br></span></font></p><p style=\"margin: 10px 0px; line-height: 26px; text-align: justify;\"><font color=\"#000000\" face=\"SolaimanLipiNormal, Helvetica, Arial, sans-serif\"><span style=\"font-size: 18px;\">গত ২৪ ঘণ্টায় যুক্তরাষ্ট্রে নতুন করে করোনাভাইরাসে আক্রান্ত হয়েছেন ১৭ হাজার ৬৬ জন এবং মারা গেছেন ৩১৭ জন। করোনাভাইরাসে সবচেয়ে ক্ষতিগ্রস্ত এই দেশটিতে এখন পর্যন্ত ৮ কোটি ১২ লাখ ১৫ হাজার ১১৮ জন করোনায় আক্রান্ত হয়েছেন এবং ৯ লাখ ৯১ হাজার ২৮ জন মারা গেছেন।</span></font></p><p style=\"margin: 10px 0px; line-height: 26px; text-align: justify;\"><font color=\"#000000\" face=\"SolaimanLipiNormal, Helvetica, Arial, sans-serif\"><span style=\"font-size: 18px;\"><br></span></font></p><p style=\"margin: 10px 0px; line-height: 26px; text-align: justify;\"><font color=\"#000000\" face=\"SolaimanLipiNormal, Helvetica, Arial, sans-serif\"><span style=\"font-size: 18px;\">এছাড়া জার্মানিতে নতুন করে করোনাভাইরাসে আক্রান্ত হয়েছেন ১ লাখ ১ হাজার ৮৭২ জন এবং মারা গেছেন ১৩৬ জন। করোনা মহামারির শুরু থেকে ইউরোপের এই দেশটিতে এখন পর্যন্ত ১ কোটি ৭৩ লাখ ৪২ হাজার ৩৫১ জন করোনায় আক্রান্ত হয়েছেন এবং ১ লাখ ২৬ হাজার ২৪৫ জন মারা গেছেন।</span></font></p><p style=\"margin: 10px 0px; line-height: 26px; text-align: justify;\"><font color=\"#000000\" face=\"SolaimanLipiNormal, Helvetica, Arial, sans-serif\"><span style=\"font-size: 18px;\"><br></span></font></p><p style=\"margin: 10px 0px; line-height: 26px; text-align: justify;\"><font color=\"#000000\" face=\"SolaimanLipiNormal, Helvetica, Arial, sans-serif\"><span style=\"font-size: 18px;\">লাতিন আমেরিকার দেশ ব্রাজিল করোনায় আক্রান্তের দিক থেকে তৃতীয় ও মৃত্যুর সংখ্যায় তালিকার দ্বিতীয় অবস্থানে রয়েছে। গত ২৪ ঘণ্টায় দেশটিতে করোনায় আক্রান্ত হয়ে মারা গেছেন ১৮৭ জন এবং নতুন করে করোনায় সংক্রমিত হয়েছেন ১৩ হাজার ৪২০ জন। অপরদিকে মহামারির শুরু থেকে এ পর্যন্ত দেশটিতে মোট শনাক্ত রোগীর সংখ্যা ২ কোটি ৯৩ লাখ ৮২ হাজার ১৯৬ জন এবং মৃত্যু হয়েছে ৬ লাখ ৫৫ হাজার ৩২৬ জনের।</span></font></p><p style=\"margin: 10px 0px; line-height: 26px; text-align: justify;\"><font color=\"#000000\" face=\"SolaimanLipiNormal, Helvetica, Arial, sans-serif\"><span style=\"font-size: 18px;\"><br></span></font></p><p style=\"margin: 10px 0px; line-height: 26px; text-align: justify;\"><font color=\"#000000\" face=\"SolaimanLipiNormal, Helvetica, Arial, sans-serif\"><span style=\"font-size: 18px;\">করোনায় আক্রান্তের তালিকায় দ্বিতীয় অবস্থানে রয়েছে প্রতিবেশী দেশ ভারত। তবে ভাইরাসে আক্রান্ত হয়ে মৃতের সংখ্যার তালিকায় দেশটির অবস্থান তৃতীয়। গত ২৪ ঘণ্টায় দেশটিতে করোনায় আক্রান্ত হয়ে মারা গেছেন ৯৮ জন এবং নতুন করে করোনায় সংক্রমিত হয়েছেন ২ হাজার ৬৬ জন। মহামারির শুরু থেকে দেশটিতে এখন পর্যন্ত মোট আক্রান্ত হয়েছেন ৪ কোটি ২৯ লাখ ৯৫ হাজার ৫৬০ জন এবং মারা গেছেন ৫ লাখ ১৬ হাজার ৫ জন।</span></font></p><p style=\"margin: 10px 0px; line-height: 26px; text-align: justify;\"><font color=\"#000000\" face=\"SolaimanLipiNormal, Helvetica, Arial, sans-serif\"><span style=\"font-size: 18px;\"><br></span></font></p><p style=\"margin: 10px 0px; line-height: 26px; text-align: justify;\"><font color=\"#000000\" face=\"SolaimanLipiNormal, Helvetica, Arial, sans-serif\"><span style=\"font-size: 18px;\">গত ২৪ ঘণ্টায় তুরস্কে নতুন করে করোনাভাইরাসে আক্রান্ত হয়েছেন ২৪ হাজার ৪০৪ জন এবং মারা গেছেন ১৩৩ জন। একই সময়ে ইতালিতে নতুন করে ভাইরাসে আক্রান্ত হয়েছেন ২৮ হাজার ৯০০ জন এবং মারা গেছেন ১২৯ জন। ইন্দোনেশিয়ায় নতুন করে করোনাভাইরাসে আক্রান্ত হয়েছেন ৯ হাজার ৬২৯ জন এবং মারা গেছেন ২৭১ জন।</span></font></p><p style=\"margin: 10px 0px; line-height: 26px; text-align: justify;\"><font color=\"#000000\" face=\"SolaimanLipiNormal, Helvetica, Arial, sans-serif\"><span style=\"font-size: 18px;\"><br></span></font></p><p style=\"margin: 10px 0px; line-height: 26px; text-align: justify;\"><font color=\"#000000\" face=\"SolaimanLipiNormal, Helvetica, Arial, sans-serif\"><span style=\"font-size: 18px;\">গত একদিনে ফ্রান্সে নতুন করে করোনাভাইরাসে আক্রান্ত হয়েছেন ১৮ হাজার ৮৫৩ জন এবং মারা গেছেন ১৮৫ জন। করোনা মহামারির শুরু থেকে দেশটিতে এখন পর্যন্ত ২ কোটি ৩৫ লাখ ৩২ হাজার ৯৯৭ জন করোনায় আক্রান্ত হয়েছেন এবং ১ লাখ ৪০ হাজার ২৯৪ জন মারা গেছেন। গত একদিনে জাপানে নতুন করে করোনাভাইরাসে আক্রান্ত হয়েছেন ৫২ হাজার ২ জন এবং মারা গেছেন ১২৫ জন।</span></font></p><p style=\"margin: 10px 0px; line-height: 26px; text-align: justify;\"><font color=\"#000000\" face=\"SolaimanLipiNormal, Helvetica, Arial, sans-serif\"><span style=\"font-size: 18px;\"><br></span></font></p><p style=\"margin: 10px 0px; line-height: 26px; text-align: justify;\"><font color=\"#000000\" face=\"SolaimanLipiNormal, Helvetica, Arial, sans-serif\"><span style=\"font-size: 18px;\">এছাড়া করোনায় আক্রান্ত হয়ে গত ২৪ ঘণ্টায় আর্জেন্টিনায় ৭০ জন, ইরানে ১১৪ জন, মালয়েশিয়ায় ৯২ জন, চিলিতে ১২৬ জন এবং থাইল্যান্ডে ৬৯ জন মারা গেছেন। অন্যদিকে গত ২৪ ঘণ্টায় মেক্সিকোতে মারা গেছেন ৪৯ জন। মহামারির শুরু থেকে এ পর্যন্ত উত্তর আমেরিকার এই দেশটিতে মৃত্যু হয়েছে ৩ লাখ ২১ হাজার ১০৩ জনের।</span></font></p><p style=\"margin: 10px 0px; line-height: 26px; text-align: justify;\"><font color=\"#000000\" face=\"SolaimanLipiNormal, Helvetica, Arial, sans-serif\"><span style=\"font-size: 18px;\"><br></span></font></p><p style=\"margin: 10px 0px; line-height: 26px; text-align: justify;\"><font color=\"#000000\" face=\"SolaimanLipiNormal, Helvetica, Arial, sans-serif\"><span style=\"font-size: 18px;\">একুশে সংবাদ/এসএস</span></font></p>', 'kronay-akrant-sare-11-lakh-mrritzu-aroo-4-hajar', '1647346514.jpg', NULL, 1, '1', '2022-03-14 23:44:03', '2022-03-29 22:37:06', '1', NULL, '1', NULL, '2', 1, 1, NULL, NULL, NULL),
(7, 'ইউক্রেনে টিভি টাওয়ারে বোমা হামলায় নিহত ৯', 3, '<p><span style=\"color: rgb(0, 0, 0); font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif; font-size: 18px; text-align: justify;\">ইউক্রেনে টেলিভিশন টাওয়ারে রাশিয়ার বোমা হামলায় অন্তত ৯ জন নিহত হয়েছেন। গুরুতর আহত হয়েছেন আরও ৯ জন। খবর আল-জাজিরার।</span><br></p>', '<p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\">স্থানীয় সময় সোমবার (১৪ মার্চ) সন্ধ্যায় ইউক্রেনের পশ্চিমাঞ্চলের শহর রিভনে ভয়াবহ এ হামলার ঘটনা ঘটে।</p><p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\">রিভনের আঞ্চলিক প্রশাসনিক প্রধান ভিটালি কোভাল এক টেলিগ্রাম বার্তায় এ তথ্য নিশ্চিত করেছেন। তিনি বার্তায় লিখেছেন, ‘টেলিভিশন টাওয়ার হামলা হয়েছে। ৯ জন ঘটনাস্থলে মারা গেছেন। আরও ৯ জন গুরুতর আহত। তাদের হাসপাতালে নেওয়া হয়েছে। হতাহতের সংখ্যা আরও বাড়তে পারে।’<br>গত ২৪ ফেব্রুয়ারি ইউক্রেনে সামরিক অভিযান শুরু করে রাশিয়ান বাহিনী। এ অভিযানে এখন পর্যন্ত দেশটিতে ৬৩৬ জন বেসামরিক নাগরিক নিহত হয়েছেন। এর মধ্যে ৯০ জন শিশু রয়েছে। এছাড়া আরও শতাধিক শিশু গুরুতর আহত হয়েছে।</p>', 'iukrene-tivi-taware-boma-hamlay-niht-9', '1647323126.jpeg', NULL, NULL, '1', '2022-03-14 23:45:26', '2022-03-14 23:45:26', NULL, NULL, '1', NULL, '0', NULL, 1, NULL, NULL, NULL),
(8, 'মায়োর্কাকে ৩-০ গোলে হারাল রিয়াল', 5, '<p><em style=\"color: rgb(0, 0, 0); font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif; font-size: 18px; text-align: justify;\"><span style=\"font-weight: bolder;\">প্রতিপক্ষের মাঠে লা লিগার ম্যাচটি মায়োর্কাকে ৩-০ গোলে হারায় রিয়াল। এই জয়ে সেভিয়ার চেয়ে ১০ পয়েন্টে এগিয়ে গেল রিয়াল। ২৮ ম্যাচে ২০ জয় ও ৬ ড্রয়ে তাদের পয়েন্ট হলো ৬৬। সমান ম্যাচে সেভিয়ার পয়েন্ট ৫৬।</span></em><br></p>', '<p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\">পিএসজির বিপক্ষে ৩-১ গোলের রোমাঞ্চকর জয়ের আত্মবিশ্বাস নিয়েই এদিন মাঠে নেমেছিল তারা। আক্রমণ-পাল্টা আক্রমনের মধ্য দিয়ে প্রথমার্ধের খেলা গোল শুন্য থাকে। দ্বিতীয়ার্ধের দশম মিনিটে গোলের দেখা পায় রিয়াল। মায়োর্কার মিডফিল্ডার ইদ্রিসু বাবা নিজেদের ডি-বক্সের বাইরে ফেদে ভালভেরদের চ্যালেঞ্জে পজেশন হারান। সেই সুযোগে বেনজেমা বল ধরে বাঁ দিকে ভিনিসিউসকে খুঁজে নেন। ফলে বিনা বাধায় মাটি কামরানো শটে গোলরক্ষকের পায়ের ফাঁক গলে দলকে এগিয়ে নেন তিনি।</p><p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\">ম্যাচের ৬৯তম মিনিটে দারুণ সুযোগ মিস করে মার্কা। ৭৭তম মিনিটে সফল স্পট কিকে ব্যবধান দ্বিগুণ করেন বেনজেমা। ডি-বক্সে ভিনিসিউস ফাউলের শিকার হলে পেনাল্টিটি পায় তারা। পাঁচ মিনিট পরই বদলি ডিফেন্ডার মার্সেলোর ক্রসে দারুণ হেডে জয় প্রায় নিশ্চিত করে ফেলেন ফরাসি ফরোয়ার্ড।</p><p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\"><br></p><p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\"><br></p><p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\">লা লিগায় টানা চার ও সব প্রতিযোগিতা মিলিয়ে টানা পাঁচ ম্যাচে গোল করেন বেনজেমা। পাঁচ ম্যাচে আট গোল করলেন তিনি। লা লিগায় সর্বোচ্চ ২২ গোল নিয়ে শীর্ষে ৩৪ বছর বয়সী তারকা। চ্যাম্পিয়ন্স লিগে দুর্দান্ত পারফরম্যান্সের পর এই দারুণ জয়। আগামী সপ্তাহে ক্লাসিকোয় চিরপ্রতিদ্বন্দ্বী বার্সেলোনার মুখোমুখি হবে রিয়াল।</p><p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\">একুশে সংবাদ/এসএস</p>', 'mayorkake-3-0-gole-haral-riyal', '1647323284.jpg', NULL, NULL, '1', '2022-03-14 23:48:04', '2022-03-14 23:48:04', NULL, NULL, NULL, '1', '0', NULL, 1, NULL, NULL, NULL),
(9, 'ক্রিস্টালে হোঁচট খেল ম্যানচেস্টার সিটি', 5, '<p>সোমবার রাতে প্রিমিয়ার লিগের ক্রিস্টাল প্যালেস-ম্যানচেস্টার সিটি ম্যাচটি গোলশূন্য ড্র হয়েছে। দুই জয়ের পর ড্র করল সিটি। চলতি আসরে চার ম্যাচে পয়েন্ট ভাগাভাগি করল পেপ গুয়ার্দিওলার দল। সিটি টেবিলের শীর্ষে থাকলেও দ্বিতীয় স্থানে থাকা লিভারপুলের সঙ্গে তাদের ব্যবধান কমার সম্ভাবনা তৈরি হয়েছে। ২৯ ম্যাচে সিটির পয়েন্ট ৭০। এক ম্যাচ কম খেলা পয়েন্ট ৬৬ লিভারপুলের।<br></p>', '<p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\">ম্যাচে প্রথমার্ধে ৭৪ শতাংশ সময় বল দখলে রেখে গোলের উদ্দেশ্যে ১৩টি শট নেয় সিটি। যার চারটি ছিল লক্ষ্যে। পরের ভাগে আরও পাঁচটি শট নিলেও একটিও লক্ষ্যে রাখতে পারেনি তারা। এদিন প্রথম ভালো সুযোগ নষ্ট হয় চতুর্দশ মিনিটে। ২৮তম মিনিটে তাদের আরেকটি ভালো প্রচেষ্টা মাটি হয়ে যায় ভাগ্যের ফেরে। ৩০ গজ দূর থেকে জোয়াও কানসেলোর বুলেট গতির শট পোস্টে লাগে।&nbsp;</p><p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\">প্রথমার্ধে লক্ষ্যে কোনো শটই রাখতে পারেনি আগের লিগ ম্যাচে উলভারহ্যাম্পটন ওয়ান্ডারার্সকে হারানো ক্রিস্টাল প্যালেস। দ্বিতীয়ার্ধের শুরু থেকে প্যালেসের রক্ষণে প্রচণ্ড চাপ দিতে থাকে সিটি। ৫৮তম মিনিটে ফের পোস্ট দাঁড়ায় পথ আগলে। ৭১তম মিনিটে ফের সুবর্ণ সুযোগ নষ্ট হয় সিটির। ৮৫তম মিনিটে উইলফ্রেদ জাহার শট আটকান এদেরসন। সিটির পোস্টে এটাই ছিল ক্রিস্টাল প্যালেসের প্রথম আক্রমণ!&nbsp;</p><p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\"><br></p><p style=\"margin: 10px 0px; line-height: 26px; text-align: justify;\"><font color=\"#000000\" face=\"SolaimanLipiNormal, Helvetica, Arial, sans-serif\"><span style=\"font-size: 18px;\">খেলার শেষ দিকে ফ্রি কিকে লাপোর্তের হেড লক্ষ্যভ্রষ্ট হলে পয়েন্ট হারানোর হতাশা নিয়ে মাঠ ছাড়তে হয় সিটিকে। ১৩ ম্যাচ ড্র করা ক্রিস্টাল প্যালেস ৩৩ পয়েন্ট নিয়ে আছে একাদশ স্থানে। জয়ের পথে থাকতে না পারলেও সিটিকে রুখে লিগ শিরোপার লড়াই জমিয়ে দিয়েছে তারা।</span></font></p><p style=\"margin: 10px 0px; line-height: 26px; text-align: justify;\"><font color=\"#000000\" face=\"SolaimanLipiNormal, Helvetica, Arial, sans-serif\"><span style=\"font-size: 18px;\"><br></span></font></p><p style=\"margin: 10px 0px; line-height: 26px; text-align: justify;\"><font color=\"#000000\" face=\"SolaimanLipiNormal, Helvetica, Arial, sans-serif\"><span style=\"font-size: 18px;\">একুশে সংবাদ/এসএস</span></font></p>', 'kristale-honnct-khel-mzancestar-siti', '1647323428.jpg', NULL, NULL, '1', '2022-03-14 23:50:28', '2022-03-14 23:50:28', NULL, NULL, NULL, '1', '0', NULL, 1, NULL, NULL, NULL),
(10, 'সানি লিওনের আগমনের সঙ্গে তথ্য মন্ত্রণালয়ের সম্পৃক্ততা নেই', 1, '<p><em style=\"color: rgb(0, 0, 0); font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif; font-size: 18px; text-align: justify;\"><span style=\"font-weight: bolder;\">বলিউড অভিনেত্রী সানি লিওনের বাংলাদেশে আসার সঙ্গে নিজেদের কোনো সম্পৃক্ততা নেই বলে দাবি করেছে তথ্য ও সম্প্রচার মন্ত্রণালয়। রোববার (১৩ মার্চ) তথ্য ও সম্প্রচার মন্ত্রণালয় থেকে এক বিজ্ঞপ্তিতে এ তথ্য জানানো হয়।</span></em><br></p>', '<p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\">চলচ্চিত্র-১ শাখার উপসচিব মো. সাইফুল ইসলাম স্বাক্ষরিত বিজ্ঞপ্তিতে বলা হয়, তথ্য ও সম্প্রচার মন্ত্রণালয়ের ২ মার্চ এর ৫২ নং স্মারকের মাধ্যমে ‘সোলজার’ নামক চলচ্চিত্রে কাজের জন্য আমেরিকান অভিনেত্রী করনজিৎ কর ওয়েভারের অনুকূলে বাংলাদেশের ওয়ার্ক পারমিট দেওয়া হয়। পরবর্তীতে দেখা যায়, এ আমেরিকান অভিনেত্রী প্রকৃতপক্ষে সানি লিওন। সঠিক তথ্য গোপন করে ওয়ার্ক পারমিট নেওয়ার বিষয়টি কর্তৃপক্ষের নজরে আসলে তার ওয়ার্ক পারমিট বাতিল করা হয়।</p><p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\">বিশ্বস্ত সূত্রের বরাত দিয়ে এতে আরও বলা হয়, সানি লিওন গানবাংলা টেলিভিশনের আমন্ত্রণে বাংলাদেশে এসেছেন। এই আগমনের সঙ্গে তথ্য ও সম্প্রচার মন্ত্রণালয়ের কোনো সম্পৃক্ততা নেই।</p>', 'sani-liooner-agmner-sngoe-tthz-mntrnalyer-smprriktta-nei', '1647323529.jpg', 3, 18, '1', '2022-03-14 23:52:09', '2022-04-10 14:20:02', NULL, NULL, NULL, '1', '1', 1, 1, NULL, NULL, NULL),
(11, 'ফের আইসিইউতে ক্রিকেটার মোশাররফ রুবেল', 5, '<p><em style=\"color: rgb(0, 0, 0); font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif; font-size: 18px; text-align: justify;\"><span style=\"font-weight: bolder;\">জাতীয় দলের ক্রিকেটার মোশাররফ হোসেন রুবেল আবারও আইসিইউতে। গত বছরের অক্টোবরে শারীরিক অবস্থা খারাপ হওয়ায় আইসিইউতে নেওয়া হয়েছিল। সেখান থেকে সুস্থ হয়ে চিকিৎসার জন্য দেশের বাইরে গিয়েছিলেন তিনি। সবশেষ গত ২০ ফেব্রুয়ারি ভারত থেকে চিকিৎসা শেষে দেশে আসেন। দেশে ফিরে আবার অসুস্থ হয়ে পড়েছেন।</span></em><br></p>', '<p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\">ক্রিকেটার মোশাররফ রুবেলের বর্তমান অবস্থা জানিয়ে ক্রিকেটার্স ওয়ালফেয়ার অ্যাসোসিয়েশনের (কোয়াব) সাধারণ সম্পাদক দেবব্রত পাল বলেন,‘শারীরিক অবস্থা কিছুটা অবনতি হওয়ায় মোশাররফকে আইসিউইতে নেওয়া হয়েছে। তার বর্তমান অবস্থা স্থিতিশীল।’</p><p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\">২০১৯ সালে ব্রেইন (মস্তিষ্কে) টিউমার ধরা পড়ে মোশাররফ রুবেলের। এরপর চিকিৎসা নিয়ে প্রায় সেরে উঠলেও ফের নতুন করে টিউমার ধরা পড়ে রুবেলের মস্তিষ্কে। নতুন করে টিউমার ধরা পড়ায় শঙ্কায় পড়ে যায় রুবেলের জীবন। জীবন-মৃত্যুর সন্ধিক্ষণে থাকা এই ক্রিকেটারকে উন্নত চিকিৎসার জন্য সিঙ্গাপুরেও নেয়া হয়।</p><p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\"><br></p><p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\">অবস্থার উন্নতি না  হওয়াতে গত বছরের নভেম্বরের দ্বিতীয় সপ্তাহে ভারতের একটি হাসপাতালে আবারও অস্ত্রোপচারের মাধ্যমে রুবেলের ব্রেন টিউমার অপসারণ করা হয়। তাতেও খুব একটা উন্নতি হয়নি। বয়সটা কেবল ৩৯-এ পড়েছে।</p><p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\">একুশে সংবাদ/এসএস</p>', 'fer-aisiiute-kriketar-mosarrf-rubel', '1647323624.jpg', NULL, 1, '1', '2022-03-14 23:53:44', '2022-03-15 02:54:44', NULL, NULL, NULL, NULL, '0', 1, 1, NULL, '1', NULL),
(12, 'ইউক্রেনের প্রতিরক্ষা শিল্পে হামলার ঘোষণা রাশিয়ার', 3, '<p><em style=\"color: rgb(0, 0, 0); font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif; font-size: 18px; text-align: justify;\"><span style=\"font-weight: bolder;\">ইউক্রেনের প্রতিরক্ষা শিল্পের সঙ্গে সংশ্লিষ্ট প্রতিষ্ঠানগুলোর কারাখানা লক্ষ্য করে হামলা চালানো হবে বলে ঘোষণা দিয়েছে রাশিয়া। সোমবার (১৪ মার্চ) দোনেতস্ক অঞ্চলে ইউক্রেনের একটি ব্যালিস্টিক ক্ষেপণাস্ত্র হামলার প্রতিবাদে এ সিদ্ধান্ত নেওয়া হয়েছে বলে দাবি করেছে মস্কো।</span></em><br></p>', '<p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\">গত মাসে ইউক্রেনের পূর্বাঞ্চলীয় লুহানস্ক ও দোনেতস্ক অঞ্চলকে স্বাধীন রাষ্ট্র হিসেবে স্বীকৃতি দেয় রাশিয়া। মঙ্গলবার (১৫ মার্চ) এক প্রতিবেদনে এই তথ্য জানিয়েছে ব্রিটিশ সংবাদমাধ্যম বিবিসি।</p><p style=\"margin: 10px 0px; color: rgb(0, 0, 0); font-size: 18px; line-height: 26px; text-align: justify; font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\"><br>রাশিয়ার প্রতিরক্ষা মন্ত্রণালয় এক অনলাইন পোস্টে জানিয়েছে, ‘দোনেতস্ক অঞ্চলে ইউক্রেনের একটি টচকা-ইউ ক্ষেপণাস্ত্র আঘাত হানার পর দেশটির প্রতিরক্ষা শিল্পকে লক্ষ্য করে অভিযান চালানোর সিদ্ধান্ত নেওয়া হয়েছে। ইউক্রেনের কট্টর জাতীয়তাবাদীরা যাতে এসব প্রতিরক্ষা শিল্প থেকে সুবিধা নিতে না পারে সেজন্য এগুলো ধ্বংস করে দেওয়া হবে।’</p><div class=\"adv-img text-center marginTopBottom20 hidden-print\" style=\"color: rgb(0, 0, 0); font-family: SolaimanLipiNormal, Helvetica, Arial, sans-serif;\"><div id=\"div-gpt-ad-1509258458742-11\"><ins class=\"adsbygoogle\" data-ad-client=\"ca-pub-5219299595808889\" data-ad-slot=\"2982997191\" data-ad-format=\"auto\" data-full-width-responsive=\"true\" data-adsbygoogle-status=\"done\" data-ad-status=\"unfilled\" style=\"display: block; height: 0px;\"><ins id=\"aswift_1_expand\" tabindex=\"0\" title=\"Advertisement\" aria-label=\"Advertisement\" style=\"border: none; height: 0px; width: 761px; margin: 0px; padding: 0px